import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  NoSymbolIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon,
} from '@heroicons/react/24/outline';

export default function GerenciarCorretores() {
  const [corretores, setCorretores] = useState([
    {
      id: 1,
      nome: 'João Silva',
      email: 'joao@exemplo.com',
      status: 'active',
      leadsAtivos: 5,
      conversao: '75%',
      ultimaAtividade: '2h atrás',
      punido: false,
      tempoPunicao: 0,
    },
    // Adicione mais corretores de exemplo aqui
  ]);
  const navigate = useNavigate();

  const handlePunish = (corretorId) => {
    const tempoPunicao = window.prompt('Por quantas horas deseja punir o corretor?');
    if (tempoPunicao && !isNaN(tempoPunicao)) {
      setCorretores(corretores.map(corretor => 
        corretor.id === corretorId ? {
          ...corretor,
          punido: true,
          tempoPunicao: parseInt(tempoPunicao),
          status: 'punished'
        } : corretor
      ));
    }
  };

  const handleRemovePunishment = (corretorId) => {
    setCorretores(corretores.map(corretor => 
      corretor.id === corretorId ? {
        ...corretor,
        punido: false,
        tempoPunicao: 0,
        status: 'active'
      } : corretor
    ));
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold text-gray-900">Gerenciar Corretores</h1>
          <button
            onClick={() => navigate('/dashboard-gestor')}
            className="text-primary-600 hover:text-primary-700"
          >
            Voltar
          </button>
        </div>

        {/* Corretores Table */}
        <div className="bg-white shadow overflow-hidden sm:rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">
              Corretores Ativos
            </h3>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">
              Gerencie os corretores e suas permissões
            </p>
          </div>
          <div className="border-t border-gray-200">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Corretor
                  </th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Leads Ativos
                  </th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Taxa de Conversão
                  </th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Última Atividade
                  </th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Ações
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {corretores.map((corretor) => (
                  <tr key={corretor.id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {corretor.nome}
                          </div>
                          <div className="text-sm text-gray-500">
                            {corretor.email}
                          </div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        corretor.status === 'active'
                          ? 'bg-green-100 text-green-800'
                          : 'bg-red-100 text-red-800'
                      }`}>
                        {corretor.status === 'active' ? 'Ativo' : 'Punido'}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {corretor.leadsAtivos}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {corretor.conversao}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {corretor.ultimaAtividade}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      {corretor.punido ? (
                        <div className="flex items-center space-x-2">
                          <span className="text-gray-500">
                            <ClockIcon className="h-5 w-5 inline mr-1" />
                            {corretor.tempoPunicao}h
                          </span>
                          <button
                            onClick={() => handleRemovePunishment(corretor.id)}
                            className="text-green-600 hover:text-green-900"
                          >
                            <CheckCircleIcon className="h-5 w-5" />
                          </button>
                        </div>
                      ) : (
                        <button
                          onClick={() => handlePunish(corretor.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          <NoSymbolIcon className="h-5 w-5" />
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
