import React, { useState, useEffect } from 'react';
import { 
  CalendarIcon, 
  PlusIcon,
  BellIcon,
  TrashIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { auth, db } from '../../firebase';
import { 
  collection, 
  query, 
  where, 
  getDocs,
  addDoc,
  deleteDoc,
  doc,
  Timestamp,
  orderBy,
  onSnapshot
} from 'firebase/firestore';

export default function Agenda() {
  const [eventos, setEventos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [novoEvento, setNovoEvento] = useState({
    titulo: '',
    descricao: '',
    data: '',
    hora: '',
    lembrete: '30'
  });

  // Verificar autenticação ao montar o componente
  useEffect(() => {
    const unsubscribeAuth = auth.onAuthStateChanged(async (user) => {
      if (user) {
        try {
          await user.reload();
          console.log('Usuário autenticado:', user.uid);
        } catch (error) {
          console.error('Erro ao recarregar usuário:', error);
          setError('Erro de autenticação. Por favor, faça login novamente.');
        }
      } else {
        setError('Você precisa estar logado para acessar a agenda.');
      }
    });

    return () => unsubscribeAuth();
  }, []);

  // Carregar eventos em tempo real
  useEffect(() => {
    if (!auth.currentUser) {
      setLoading(false);
      return;
    }

    try {
      const eventosRef = collection(db, 'eventos');
      const q = query(
        eventosRef,
        where('usuario_id', '==', auth.currentUser.uid),
        orderBy('data', 'asc')
      );

      const unsubscribe = onSnapshot(q, 
        (snapshot) => {
          const eventosData = snapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data()
          }));
          setEventos(eventosData);
          setLoading(false);
        },
        (error) => {
          console.error('Erro ao carregar eventos:', error);
          setError('Erro ao carregar eventos. Por favor, recarregue a página.');
          setLoading(false);
        }
      );

      return () => unsubscribe();
    } catch (error) {
      console.error('Erro ao configurar listener:', error);
      setError('Erro ao configurar monitoramento de eventos.');
      setLoading(false);
    }
  }, [auth.currentUser]);

  const criarEvento = async (e) => {
    e.preventDefault();
    
    try {
      // Verificar e recarregar usuário
      if (!auth.currentUser) {
        setError('Você precisa estar logado para criar eventos.');
        return;
      }

      await auth.currentUser.reload();
      const user = auth.currentUser;
      
      if (!user) {
        setError('Sessão expirada. Por favor, faça login novamente.');
        return;
      }

      setSaving(true);
      setError(null);

      const dataHora = new Date(novoEvento.data + 'T' + novoEvento.hora);
      
      if (dataHora < new Date()) {
        setError('A data do evento não pode ser no passado.');
        setSaving(false);
        return;
      }

      const eventoData = {
        usuario_id: user.uid,
        titulo: novoEvento.titulo,
        descricao: novoEvento.descricao,
        data: Timestamp.fromDate(dataHora),
        lembrete_minutos: parseInt(novoEvento.lembrete),
        created_at: Timestamp.now(),
        email_usuario: user.email // Adicionar email do usuário para referência
      };

      console.log('Criando evento:', {
        ...eventoData,
        usuario: {
          uid: user.uid,
          email: user.email
        }
      });

      const eventosRef = collection(db, 'eventos');
      await addDoc(eventosRef, eventoData);

      setNovoEvento({
        titulo: '',
        descricao: '',
        data: '',
        hora: '',
        lembrete: '30'
      });
      setShowForm(false);
      setError(null);

    } catch (error) {
      console.error('Erro ao criar evento:', error);
      if (error.code === 'permission-denied') {
        setError('Você não tem permissão para criar eventos. Por favor, faça login novamente.');
      } else {
        setError('Erro ao criar evento. Por favor, tente novamente.');
      }
    } finally {
      setSaving(false);
    }
  };

  const excluirEvento = async (eventoId) => {
    if (!window.confirm('Tem certeza que deseja excluir este evento?')) return;

    try {
      await deleteDoc(doc(db, 'eventos', eventoId));
    } catch (error) {
      console.error('Erro ao excluir evento:', error);
      setError('Erro ao excluir evento. Por favor, tente novamente.');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Minha Agenda</h1>
          <p className="text-sm text-gray-500 mt-1">Gerencie seus compromissos</p>
        </div>
        <button
          onClick={() => setShowForm(true)}
          className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700"
        >
          <PlusIcon className="h-5 w-5 mr-2" />
          Novo Evento
        </button>
      </div>

      {error && (
        <div className="mb-4 p-4 rounded-md bg-red-50 border border-red-200 text-red-700 text-sm">
          {error}
        </div>
      )}

      {showForm && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium">Novo Evento</h3>
              <button onClick={() => setShowForm(false)} className="text-gray-400 hover:text-gray-500">
                <XMarkIcon className="h-5 w-5" />
              </button>
            </div>

            <form onSubmit={criarEvento} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Título</label>
                <input
                  type="text"
                  required
                  value={novoEvento.titulo}
                  onChange={(e) => setNovoEvento({...novoEvento, titulo: e.target.value})}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Descrição</label>
                <textarea
                  value={novoEvento.descricao}
                  onChange={(e) => setNovoEvento({...novoEvento, descricao: e.target.value})}
                  rows="3"
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Data</label>
                  <input
                    type="date"
                    required
                    value={novoEvento.data}
                    onChange={(e) => setNovoEvento({...novoEvento, data: e.target.value})}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Hora</label>
                  <input
                    type="time"
                    required
                    value={novoEvento.hora}
                    onChange={(e) => setNovoEvento({...novoEvento, hora: e.target.value})}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Lembrete</label>
                <select
                  value={novoEvento.lembrete}
                  onChange={(e) => setNovoEvento({...novoEvento, lembrete: e.target.value})}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                >
                  <option value="5">5 minutos antes</option>
                  <option value="10">10 minutos antes</option>
                  <option value="15">15 minutos antes</option>
                  <option value="30">30 minutos antes</option>
                  <option value="60">1 hora antes</option>
                  <option value="1440">1 dia antes</option>
                </select>
              </div>

              <div className="flex justify-end space-x-3 mt-6">
                <button
                  type="button"
                  onClick={() => setShowForm(false)}
                  className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
                  disabled={saving}
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-primary-600 text-white rounded-md text-sm font-medium hover:bg-primary-700 disabled:opacity-50"
                  disabled={saving}
                >
                  {saving ? 'Salvando...' : 'Salvar'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="space-y-4">
        {eventos.length === 0 ? (
          <div className="text-center py-12 bg-white rounded-lg border border-gray-200">
            <CalendarIcon className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">Sem eventos</h3>
            <p className="mt-1 text-sm text-gray-500">
              Comece criando um novo evento na sua agenda.
            </p>
          </div>
        ) : (
          eventos.map((evento) => (
            <div
              key={evento.id}
              className="bg-white rounded-lg border border-gray-200 p-4 hover:shadow-md transition-shadow"
            >
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="text-lg font-medium text-gray-900">{evento.titulo}</h3>
                  {evento.descricao && (
                    <p className="mt-1 text-sm text-gray-500">{evento.descricao}</p>
                  )}
                  <div className="mt-2 flex items-center space-x-4 text-sm text-gray-500">
                    <div className="flex items-center">
                      <CalendarIcon className="h-4 w-4 mr-1 text-gray-400" />
                      {evento.data.toDate().toLocaleDateString('pt-BR')} às{' '}
                      {evento.data.toDate().toLocaleTimeString('pt-BR', {
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </div>
                    <div className="flex items-center">
                      <BellIcon className="h-4 w-4 mr-1 text-gray-400" />
                      {evento.lembrete_minutos} minutos antes
                    </div>
                  </div>
                </div>
                <button
                  onClick={() => excluirEvento(evento.id)}
                  className="text-gray-400 hover:text-red-500"
                >
                  <TrashIcon className="h-5 w-5" />
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
