import React, { useState, useEffect } from 'react';
import { auth, db } from '../firebase';
import { 
  collection, 
  addDoc, 
  query, 
  where, 
  orderBy, 
  onSnapshot,
  serverTimestamp,
  deleteDoc,
  doc,
  getDocs
} from 'firebase/firestore';
import { 
  ChartBarIcon, 
  UserGroupIcon, 
  DocumentTextIcon,
  ShareIcon,
  ClockIcon,
  UserIcon,
  BellIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';

export default function DashboardGestor() {
  const [avisos, setAvisos] = useState([]);
  const [novoAviso, setNovoAviso] = useState('');
  const [corretores, setCorretores] = useState([]);
  const [corretorSelecionado, setCorretorSelecionado] = useState('');
  const [metricas, setMetricas] = useState({
    totalLeads: 0,
    leadsDistribuidosHoje: 0,
    leadsDevolvidos: 0,
    leadsEmAtendimento: 0
  });

  // Carregar métricas
  useEffect(() => {
    const carregarMetricas = async () => {
      try {
        // Total de Leads (todos os leads carregados)
        const leadsRef = collection(db, 'leads');
        const leadsSnapshot = await getDocs(leadsRef);
        const totalLeads = leadsSnapshot.size;

        // Leads Distribuídos Hoje
        const hoje = new Date();
        hoje.setHours(0, 0, 0, 0);
        const leadsHojeQuery = query(
          leadsRef,
          where('dataDistribuicao', '>=', hoje)
        );
        const leadsHojeSnapshot = await getDocs(leadsHojeQuery);
        const leadsDistribuidosHoje = leadsHojeSnapshot.size;

        // Leads Devolvidos
        const leadsDevolvidosQuery = query(
          leadsRef,
          where('status', '==', 'devolvido')
        );
        const leadsDevolvidosSnapshot = await getDocs(leadsDevolvidosQuery);
        const leadsDevolvidos = leadsDevolvidosSnapshot.size;

        // Leads em Atendimento
        const leadsAtendimentoQuery = query(
          leadsRef,
          where('status', '==', 'em_atendimento')
        );
        const leadsAtendimentoSnapshot = await getDocs(leadsAtendimentoQuery);
        const leadsEmAtendimento = leadsAtendimentoSnapshot.size;

        setMetricas({
          totalLeads,
          leadsDistribuidosHoje,
          leadsDevolvidos,
          leadsEmAtendimento
        });
      } catch (error) {
        console.error('Erro ao carregar métricas:', error);
      }
    };

    carregarMetricas();
    const interval = setInterval(carregarMetricas, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  // Carregar corretores
  useEffect(() => {
    const fetchCorretores = async () => {
      try {
        const q = query(collection(db, 'users'), where('role', '==', 'corretor'));
        const snapshot = await getDocs(q);
        const data = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setCorretores(data);
      } catch (error) {
        console.error('Erro ao carregar corretores:', error);
      }
    };

    fetchCorretores();
  }, []);

  // Carregar notificações
  useEffect(() => {
    try {
      const q = query(
        collection(db, 'notificacoes'),
        where('tipo', '==', 'aviso'),
        orderBy('criadoEm', 'desc')
      );

      const unsubscribe = onSnapshot(q, (snapshot) => {
        const notificacoes = snapshot.docs
          .map(doc => ({
            id: doc.id,
            ...doc.data()
          }))
          .filter(notif => {
            const tempo = notif.criadoEm?.toDate();
            if (!tempo) return false;
            const horasPassadas = (new Date() - tempo) / (1000 * 60 * 60);
            return horasPassadas <= 4;
          });

        setAvisos(notificacoes);
      });

      return () => unsubscribe();
    } catch (error) {
      console.error('Erro ao carregar notificações:', error);
    }
  }, []);

  const enviarAviso = async (e) => {
    e.preventDefault();
    if (!novoAviso.trim() || !corretorSelecionado) return;

    try {
      const notificacaoBase = {
        tipo: 'aviso',
        mensagem: novoAviso.trim(),
        remetente: {
          id: auth.currentUser.uid,
          nome: auth.currentUser.displayName || auth.currentUser.email
        },
        criadoEm: serverTimestamp(),
        lido: false
      };

      if (corretorSelecionado === 'todos') {
        // Criar uma notificação geral
        await addDoc(collection(db, 'notificacoes'), {
          ...notificacaoBase,
          paraTodos: true
        });
      } else {
        // Criar notificação para um corretor específico
        const corretor = corretores.find(c => c.id === corretorSelecionado);
        await addDoc(collection(db, 'notificacoes'), {
          ...notificacaoBase,
          paraTodos: false,
          destinatario: {
            id: corretor.id,
            nome: corretor.name || corretor.email
          }
        });
      }

      setNovoAviso('');
      setCorretorSelecionado('');
    } catch (error) {
      console.error('Erro ao enviar notificação:', error);
    }
  };

  const deletarAviso = async (avisoId) => {
    try {
      await deleteDoc(doc(db, 'notificacoes', avisoId));
    } catch (error) {
      console.error('Erro ao deletar notificação:', error);
    }
  };

  return (
    <div className="container mx-auto px-6 py-8">
      <h3 className="text-3xl font-bold text-gray-900 mb-8">
        Visão Geral
      </h3>

      {/* Cards */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        {/* Total de Leads */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <DocumentTextIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Total de Leads
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.totalLeads}
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Leads Distribuídos Hoje */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <ShareIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Leads Distribuídos Hoje
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.leadsDistribuidosHoje}
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Leads Devolvidos */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <ClockIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Leads Devolvidos
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.leadsDevolvidos}
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Leads em Atendimento */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <UserIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Leads em Atendimento
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.leadsEmAtendimento}
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto space-y-6 mt-8">
        {/* Formulário de Novo Aviso */}
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4">Enviar Aviso</h2>
          <form onSubmit={enviarAviso} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Para
              </label>
              <select
                value={corretorSelecionado}
                onChange={(e) => setCorretorSelecionado(e.target.value)}
                className="w-full p-2 border rounded focus:ring-primary-500 focus:border-primary-500"
                required
              >
                <option value="">Selecione...</option>
                <option value="todos" className="font-semibold">Todos os Corretores</option>
                <optgroup label="Corretor Específico">
                  {corretores.map(corretor => (
                    <option key={corretor.id} value={corretor.id}>
                      {corretor.name || corretor.email}
                    </option>
                  ))}
                </optgroup>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Mensagem
              </label>
              <textarea
                value={novoAviso}
                onChange={(e) => setNovoAviso(e.target.value)}
                className="w-full p-2 border rounded focus:ring-primary-500 focus:border-primary-500"
                rows="3"
                placeholder="Digite sua mensagem..."
                required
              />
            </div>
            
            <button
              type="submit"
              className="w-full bg-primary-600 text-white py-2 px-4 rounded hover:bg-primary-700 transition-colors"
            >
              Enviar
            </button>
          </form>
        </div>

        {/* Lista de Avisos */}
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
            <BellIcon className="h-5 w-5 text-primary-500" />
            Avisos Enviados
          </h2>
          
          <div className="space-y-4">
            {avisos.length === 0 ? (
              <p className="text-gray-500 text-center py-4">
                Nenhum aviso nas últimas 4 horas
              </p>
            ) : (
              avisos.map(aviso => (
                <div 
                  key={aviso.id}
                  className="border rounded-lg p-4 relative hover:bg-gray-50"
                >
                  <button
                    onClick={() => deletarAviso(aviso.id)}
                    className="absolute top-2 right-2 text-gray-400 hover:text-red-500"
                  >
                    <XMarkIcon className="h-5 w-5" />
                  </button>
                  
                  <div>
                    <p className="font-medium text-gray-900">{aviso.mensagem}</p>
                    <p className="text-sm text-gray-500 mt-1">
                      Para: {aviso.paraTodos ? 'Todos os Corretores' : aviso.destinatario.nome}
                    </p>
                  </div>
                  
                  <div className="mt-2 text-xs text-gray-500">
                    Enviado em: {aviso.criadoEm?.toDate().toLocaleString()}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
