import React, { useState, useEffect } from 'react';
import { 
  CalendarIcon, 
  PlusIcon,
  ClockIcon,
  BellIcon,
  TrashIcon,
  ExclamationCircleIcon
} from '@heroicons/react/24/outline';
import { auth, db } from '../../firebase';
import { 
  collection, 
  query, 
  where, 
  getDocs,
  addDoc,
  deleteDoc,
  doc,
  Timestamp,
  orderBy 
} from 'firebase/firestore';

const GOOGLE_API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;
const GOOGLE_CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID;

export default function Agenda() {
  const [eventos, setEventos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [googleAuth, setGoogleAuth] = useState(null);
  const [isAuthorized, setIsAuthorized] = useState(false);
  const [novoEvento, setNovoEvento] = useState({
    titulo: '',
    descricao: '',
    data: '',
    hora: '',
    lembrete: '30' // minutos antes
  });
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    // Verificar estado da autenticação
    const unsubscribe = auth.onAuthStateChanged((user) => {
      console.log('Estado da autenticação:', user ? 'Autenticado' : 'Não autenticado');
      if (user) {
        console.log('Dados do usuário:', {
          uid: user.uid,
          email: user.email,
          emailVerified: user.emailVerified
        });
      }
    });

    // Carregar API do Google Calendar
    const script = document.createElement('script');
    script.src = 'https://apis.google.com/js/api.js';
    script.onload = () => {
      window.gapi.load('client:auth2', initializeGoogleApi);
    };
    document.body.appendChild(script);

    // Carregar eventos do Firestore
    carregarEventos();

    return () => unsubscribe();
  }, []);

  const initializeGoogleApi = async () => {
    try {
      await window.gapi.client.init({
        apiKey: GOOGLE_API_KEY,
        clientId: GOOGLE_CLIENT_ID,
        discoveryDocs: ['https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest'],
        scope: 'https://www.googleapis.com/auth/calendar.events'
      });

      const auth = window.gapi.auth2.getAuthInstance();
      setGoogleAuth(auth);
      setIsAuthorized(auth.isSignedIn.get());

      auth.isSignedIn.listen((isSignedIn) => {
        setIsAuthorized(isSignedIn);
      });
    } catch (error) {
      console.error('Erro ao inicializar Google API:', error);
    }
  };

  const handleGoogleLogin = () => {
    if (googleAuth) {
      googleAuth.signIn();
    }
  };

  const carregarEventos = async () => {
    if (!auth.currentUser) return;

    try {
      const eventosRef = collection(db, 'eventos');
      const q = query(
        eventosRef,
        where('usuario_id', '==', auth.currentUser.uid),
        orderBy('data', 'asc')
      );
      
      const snapshot = await getDocs(q);
      const eventosData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      setEventos(eventosData);
    } catch (error) {
      console.error('Erro ao carregar eventos:', error);
    } finally {
      setLoading(false);
    }
  };

  const criarEvento = async (e) => {
    e.preventDefault();
    
    // Verificar autenticação antes de criar evento
    const currentUser = auth.currentUser;
    const token = await currentUser?.getIdToken(true); // Forçar atualização do token
    
    console.log('Estado da autenticação:', {
      currentUser: currentUser ? {
        uid: currentUser.uid,
        email: currentUser.email,
        token: token?.substring(0, 20) + '...' // Log parcial do token por segurança
      } : null,
      isAuthenticated: !!currentUser
    });

    if (!currentUser) {
      setError('Você precisa estar logado para criar eventos.');
      return;
    }

    setSaving(true);
    setError(null);

    try {
      const dataHora = new Date(novoEvento.data + 'T' + novoEvento.hora);
      
      if (dataHora < new Date()) {
        setError('A data do evento não pode ser no passado.');
        setSaving(false);
        return;
      }

      const lembreteMinutos = parseInt(novoEvento.lembrete);
      
      const eventoData = {
        usuario_id: currentUser.uid,
        titulo: novoEvento.titulo,
        descricao: novoEvento.descricao,
        data: Timestamp.fromDate(dataHora),
        lembrete_minutos: lembreteMinutos,
        created_at: Timestamp.now(),
        status: 'ativo'
      };

      console.log('Tentando criar evento:', {
        colecao: 'eventos',
        dados: eventoData,
        auth: {
          uid: currentUser.uid,
          tokenExists: !!token
        }
      });

      try {
        // Tentar reautenticar antes de criar o evento
        await auth.currentUser?.reload();
        
        const eventosRef = collection(db, 'eventos');
        const eventoRef = await addDoc(eventosRef, eventoData);
        console.log('Evento criado com sucesso:', {
          id: eventoRef.id,
          path: eventoRef.path
        });

        if (isAuthorized) {
          try {
            const event = {
              'summary': novoEvento.titulo,
              'description': novoEvento.descricao,
              'start': {
                'dateTime': dataHora.toISOString(),
                'timeZone': Intl.DateTimeFormat().resolvedOptions().timeZone
              },
              'end': {
                'dateTime': new Date(dataHora.getTime() + 3600000).toISOString(),
                'timeZone': Intl.DateTimeFormat().resolvedOptions().timeZone
              },
              'reminders': {
                'useDefault': false,
                'overrides': [
                  {'method': 'popup', 'minutes': lembreteMinutos}
                ]
              }
            };

            const response = await window.gapi.client.calendar.events.insert({
              'calendarId': 'primary',
              'resource': event
            });
            console.log('Evento criado no Google Calendar:', response);
          } catch (googleError) {
            console.error('Erro ao criar evento no Google Calendar:', googleError);
            setError('Evento criado no sistema, mas houve um erro ao sincronizar com o Google Calendar.');
          }
        }

        setNovoEvento({
          titulo: '',
          descricao: '',
          data: '',
          hora: '',
          lembrete: '30'
        });
        setShowForm(false);
        await carregarEventos();
        
      } catch (firestoreError) {
        console.error('Erro detalhado do Firestore:', {
          code: firestoreError.code,
          message: firestoreError.message,
          name: firestoreError.name,
          stack: firestoreError.stack
        });
        
        if (firestoreError.code === 'permission-denied') {
          setError('Erro de permissão: Por favor, faça logout e login novamente.');
          // Tentar fazer logout e login automático
          try {
            await auth.signOut();
            setError('Você foi desconectado. Por favor, faça login novamente.');
          } catch (logoutError) {
            console.error('Erro ao fazer logout:', logoutError);
          }
        } else {
          setError(`Erro ao criar evento: ${firestoreError.message}`);
        }
        throw firestoreError;
      }

    } catch (error) {
      console.error('Erro geral ao criar evento:', error);
      setError('Erro ao criar evento. Por favor, tente novamente mais tarde.');
    } finally {
      setSaving(false);
    }
  };

  const excluirEvento = async (eventoId) => {
    if (!window.confirm('Tem certeza que deseja excluir este evento?')) return;

    try {
      await deleteDoc(doc(db, 'eventos', eventoId));
      carregarEventos();
    } catch (error) {
      console.error('Erro ao excluir evento:', error);
      alert('Erro ao excluir evento. Tente novamente.');
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-6 py-8">
        <div className="flex items-center justify-center h-64">
          <div className="text-gray-500">Carregando agenda...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-6 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Minha Agenda</h1>
        <p className="mt-2 text-gray-600">Gerencie seus compromissos e lembretes</p>
      </div>

      {error && (
        <div className="mb-4 p-4 rounded-md bg-red-50 border border-red-200">
          <div className="flex">
            <div className="flex-shrink-0">
              <ExclamationCircleIcon className="h-5 w-5 text-red-400" />
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-700">{error}</p>
            </div>
          </div>
        </div>
      )}

      {/* Botões de Ação */}
      <div className="mb-8 flex items-center space-x-4">
        <button
          onClick={() => setShowForm(true)}
          className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
        >
          <PlusIcon className="h-5 w-5 mr-2" />
          Novo Evento
        </button>

        {!isAuthorized && (
          <button
            onClick={handleGoogleLogin}
            className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            <img src="https://www.google.com/images/icons/product/calendar-32.png" alt="Google Calendar" className="h-5 w-5 mr-2" />
            Conectar ao Google Calendar
          </button>
        )}
      </div>

      {/* Formulário de Novo Evento */}
      {showForm && (
        <div className="mb-8 bg-white rounded-xl shadow-sm border border-gray-100 p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Novo Evento</h3>
          <form onSubmit={criarEvento} className="space-y-4">
            <div>
              <label htmlFor="titulo" className="block text-sm font-medium text-gray-700">
                Título
              </label>
              <input
                type="text"
                id="titulo"
                required
                value={novoEvento.titulo}
                onChange={(e) => setNovoEvento({...novoEvento, titulo: e.target.value})}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="descricao" className="block text-sm font-medium text-gray-700">
                Descrição
              </label>
              <textarea
                id="descricao"
                value={novoEvento.descricao}
                onChange={(e) => setNovoEvento({...novoEvento, descricao: e.target.value})}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label htmlFor="data" className="block text-sm font-medium text-gray-700">
                  Data
                </label>
                <input
                  type="date"
                  id="data"
                  required
                  value={novoEvento.data}
                  onChange={(e) => setNovoEvento({...novoEvento, data: e.target.value})}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                />
              </div>

              <div>
                <label htmlFor="hora" className="block text-sm font-medium text-gray-700">
                  Hora
                </label>
                <input
                  type="time"
                  id="hora"
                  required
                  value={novoEvento.hora}
                  onChange={(e) => setNovoEvento({...novoEvento, hora: e.target.value})}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                />
              </div>

              <div>
                <label htmlFor="lembrete" className="block text-sm font-medium text-gray-700">
                  Lembrete
                </label>
                <select
                  id="lembrete"
                  value={novoEvento.lembrete}
                  onChange={(e) => setNovoEvento({...novoEvento, lembrete: e.target.value})}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                >
                  <option value="5">5 minutos antes</option>
                  <option value="10">10 minutos antes</option>
                  <option value="15">15 minutos antes</option>
                  <option value="30">30 minutos antes</option>
                  <option value="60">1 hora antes</option>
                  <option value="1440">1 dia antes</option>
                </select>
              </div>
            </div>

            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={() => {
                  setShowForm(false);
                  setError(null);
                }}
                className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                disabled={saving}
              >
                Cancelar
              </button>
              <button
                type="submit"
                className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                disabled={saving}
              >
                {saving ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Criando...
                  </>
                ) : (
                  'Criar Evento'
                )}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Lista de Eventos */}
      <div className="bg-gradient-to-br from-blue-50 to-white rounded-xl shadow-lg border border-blue-100 overflow-hidden">
        <div className="p-6 border-b border-blue-100 bg-blue-500">
          <h3 className="text-lg font-semibold text-white">
            Eventos Agendados
          </h3>
        </div>

        {eventos.length === 0 ? (
          <div className="p-6 text-center">
            <CalendarIcon className="mx-auto h-12 w-12 text-blue-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">Nenhum evento agendado</h3>
            <p className="mt-1 text-sm text-gray-500">
              Clique em "Novo Evento" para criar um compromisso.
            </p>
          </div>
        ) : (
          <div className="mt-4 space-y-4">
            {eventos.map((evento) => (
              <div
                key={evento.id}
                className="p-4 rounded-lg shadow-md bg-gradient-to-r from-blue-600 to-blue-400 text-white relative hover:from-blue-700 hover:to-blue-500 transition-all duration-300"
              >
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-lg font-semibold mb-1">{evento.titulo}</h3>
                    <p className="text-blue-100 mb-2">{evento.descricao}</p>
                    <div className="flex items-center space-x-2 text-sm text-blue-50">
                      <CalendarIcon className="h-4 w-4" />
                      <span>
                        {evento.data.toDate().toLocaleDateString('pt-BR')} às{' '}
                        {evento.data.toDate().toLocaleTimeString('pt-BR', {
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                    </div>
                    <div className="flex items-center space-x-2 text-sm text-blue-50 mt-1">
                      <BellIcon className="h-4 w-4" />
                      <span>Lembrete: {evento.lembrete_minutos} minutos antes</span>
                    </div>
                  </div>
                  <button
                    onClick={() => excluirEvento(evento.id)}
                    className="text-blue-100 hover:text-white transition-colors duration-200"
                  >
                    <TrashIcon className="h-5 w-5" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
