import React, { useState, useEffect } from 'react';
import { auth, db } from '../firebase';
import { 
  collection, 
  query, 
  where, 
  orderBy, 
  onSnapshot,
  updateDoc,
  doc,
  getDocs 
} from 'firebase/firestore';
import { 
  ChartBarIcon,
  ClipboardDocumentListIcon,
  PhoneIcon,
  ChatBubbleLeftIcon,
  BellIcon,
  CheckCircleIcon,
} from '@heroicons/react/24/outline';

export default function DashboardCorretor() {
  const [avisos, setAvisos] = useState([]);
  const [avisosNaoLidos, setAvisosNaoLidos] = useState(0);
  const [metricas, setMetricas] = useState({
    leadsAtivos: 0,
    atendimentosHoje: 0,
    taxaConversao: 0,
    mensagensNaoLidas: 0
  });

  // Carregar métricas
  useEffect(() => {
    const carregarMetricas = async () => {
      if (!auth.currentUser) return;

      try {
        // Leads Ativos
        const leadsQuery = query(
          collection(db, 'leads'),
          where('corretorId', '==', auth.currentUser.uid),
          where('status', '==', 'ativo')
        );
        const leadsSnapshot = await getDocs(leadsQuery);
        const leadsAtivos = leadsSnapshot.size;

        // Atendimentos Hoje
        const hoje = new Date();
        hoje.setHours(0, 0, 0, 0);
        const atendimentosQuery = query(
          collection(db, 'atendimentos'),
          where('corretorId', '==', auth.currentUser.uid),
          where('data', '>=', hoje)
        );
        const atendimentosSnapshot = await getDocs(atendimentosQuery);
        const atendimentosHoje = atendimentosSnapshot.size;

        // Taxa de Conversão
        const leadsFechadosQuery = query(
          collection(db, 'leads'),
          where('corretorId', '==', auth.currentUser.uid),
          where('status', '==', 'convertido')
        );
        const leadsFechadosSnapshot = await getDocs(leadsFechadosQuery);
        const totalLeadsQuery = query(
          collection(db, 'leads'),
          where('corretorId', '==', auth.currentUser.uid)
        );
        const totalLeadsSnapshot = await getDocs(totalLeadsQuery);
        
        const taxaConversao = totalLeadsSnapshot.size > 0
          ? Math.round((leadsFechadosSnapshot.size / totalLeadsSnapshot.size) * 100)
          : 0;

        // Mensagens não lidas
        const mensagensQuery = query(
          collection(db, 'mensagens'),
          where('destinatarioId', '==', auth.currentUser.uid),
          where('lida', '==', false)
        );
        const mensagensSnapshot = await getDocs(mensagensQuery);
        const mensagensNaoLidas = mensagensSnapshot.size;

        setMetricas({
          leadsAtivos,
          atendimentosHoje,
          taxaConversao,
          mensagensNaoLidas
        });
      } catch (error) {
        console.error('Erro ao carregar métricas:', error);
      }
    };

    carregarMetricas();
    // Atualizar métricas a cada 5 minutos
    const interval = setInterval(carregarMetricas, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  // Carregar avisos
  useEffect(() => {
    if (!auth.currentUser) return;

    const q = query(
      collection(db, 'avisos'),
      where('corretorId', '==', auth.currentUser.uid),
      orderBy('timestamp', 'desc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const avisosData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      })).filter(aviso => {
        const avisoTime = aviso.timestamp?.toDate();
        if (!avisoTime) return false;
        const horasPassadas = (new Date() - avisoTime) / (1000 * 60 * 60);
        return horasPassadas <= 4;
      });

      setAvisos(avisosData);
      setAvisosNaoLidos(avisosData.filter(aviso => !aviso.lido).length);
    });

    return () => unsubscribe();
  }, []);

  const marcarComoLido = async (avisoId) => {
    try {
      const avisoRef = doc(db, 'avisos', avisoId);
      await updateDoc(avisoRef, {
        lido: true
      });
    } catch (error) {
      console.error('Erro ao marcar aviso como lido:', error);
    }
  };

  return (
    <div className="container mx-auto px-6 py-8">
      <h3 className="text-3xl font-bold text-gray-900 mb-8">
        Visão Geral
      </h3>

      {/* Cards */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        {/* Leads Ativos */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <ClipboardDocumentListIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Leads Ativos
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.leadsAtivos}
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Atendimentos Hoje */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <PhoneIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Atendimentos Hoje
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.atendimentosHoje}
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Taxa de Conversão */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <ChartBarIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Taxa de Conversão
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.taxaConversao}%
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Mensagens não lidas */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <ChatBubbleLeftIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Mensagens não lidas
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {metricas.mensagensNaoLidas}
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Área de Avisos */}
      <div className="mt-8 bg-white p-6 rounded-lg shadow">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold flex items-center gap-2">
            <BellIcon className="h-5 w-5 text-primary-500" />
            Avisos Recebidos
          </h2>
          {avisosNaoLidos > 0 && (
            <span className="bg-red-100 text-red-600 px-2 py-1 rounded-full text-sm">
              {avisosNaoLidos} não {avisosNaoLidos === 1 ? 'lido' : 'lidos'}
            </span>
          )}
        </div>

        <div className="space-y-4">
          {avisos.length === 0 ? (
            <p className="text-gray-500 text-center py-4">
              Nenhum aviso recebido nas últimas 4 horas
            </p>
          ) : (
            avisos.map(aviso => (
              <div 
                key={aviso.id}
                className={`border rounded-lg p-4 relative hover:bg-gray-50 ${
                  !aviso.lido ? 'bg-blue-50 border-blue-100' : ''
                }`}
              >
                {!aviso.lido && (
                  <button
                    onClick={() => marcarComoLido(aviso.id)}
                    className="absolute top-2 right-2 text-blue-500 hover:text-blue-600"
                    title="Marcar como lido"
                  >
                    <CheckCircleIcon className="h-5 w-5" />
                  </button>
                )}
                
                <div>
                  <p className="font-medium text-gray-900">{aviso.texto}</p>
                  <p className="text-sm text-gray-500 mt-1">
                    De: {aviso.gestorNome}
                  </p>
                </div>
                
                <div className="mt-2 text-xs text-gray-500">
                  Recebido em: {aviso.timestamp?.toDate().toLocaleString()}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
