import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  CloudArrowUpIcon, 
  CheckCircleIcon,
  XCircleIcon,
  UserGroupIcon,
} from '@heroicons/react/24/outline';
import Papa from 'papaparse';

export default function DistribuirLeads() {
  const [leads, setLeads] = useState([]);
  const [selectedLeads, setSelectedLeads] = useState([]);
  const [error, setError] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const navigate = useNavigate();

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    setIsUploading(true);
    setError('');

    if (file) {
      Papa.parse(file, {
        header: true,
        complete: (results) => {
          // Validar as colunas necessárias
          const requiredColumns = ['NOME', 'CNPJ', 'EMAIL', 'TELEFONE', 'IDADE', 'STATUS'];
          const headers = Object.keys(results.data[0]);
          const missingColumns = requiredColumns.filter(col => !headers.includes(col));

          if (missingColumns.length > 0) {
            setError(`Colunas obrigatórias faltando: ${missingColumns.join(', ')}`);
            setIsUploading(false);
            return;
          }

          // Validar e formatar os dados
          const validLeads = results.data.filter(lead => 
            lead.NOME && 
            lead.EMAIL && 
            lead.TELEFONE
          ).map((lead, index) => ({
            ...lead,
            id: index,
            selected: false
          }));

          setLeads(validLeads);
          setIsUploading(false);
        },
        error: (error) => {
          setError('Erro ao processar o arquivo: ' + error.message);
          setIsUploading(false);
        }
      });
    }
  };

  const toggleLeadSelection = (leadId) => {
    setLeads(leads.map(lead => 
      lead.id === leadId ? { ...lead, selected: !lead.selected } : lead
    ));
    
    const lead = leads.find(l => l.id === leadId);
    if (lead) {
      if (lead.selected) {
        setSelectedLeads(selectedLeads.filter(id => id !== leadId));
      } else {
        setSelectedLeads([...selectedLeads, leadId]);
      }
    }
  };

  const handleDistribute = () => {
    // Aqui você implementará a lógica de distribuição
    console.log('Leads selecionados:', selectedLeads.map(id => leads.find(l => l.id === id)));
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold text-gray-900">Distribuir Leads</h1>
          <button
            onClick={() => navigate('/dashboard-gestor')}
            className="text-primary-600 hover:text-primary-700"
          >
            Voltar
          </button>
        </div>

        {/* Upload Section */}
        <div className="bg-white shadow sm:rounded-lg mb-8">
          <div className="px-4 py-5 sm:p-6">
            <div className="flex items-center justify-center">
              <label className="relative cursor-pointer">
                <input
                  type="file"
                  className="sr-only"
                  accept=".csv"
                  onChange={handleFileUpload}
                  disabled={isUploading}
                />
                <div className="flex flex-col items-center justify-center space-y-2 p-6 border-2 border-dashed border-gray-300 rounded-lg hover:border-primary-500 transition-colors">
                  <CloudArrowUpIcon className="h-12 w-12 text-gray-400" />
                  <span className="text-sm text-gray-600">
                    {isUploading ? 'Processando...' : 'Clique para fazer upload do CSV'}
                  </span>
                  <span className="text-xs text-gray-500">
                    Colunas necessárias: NOME, CNPJ, EMAIL, TELEFONE, IDADE, STATUS
                  </span>
                </div>
              </label>
            </div>
          </div>
        </div>

        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-8">
            <div className="flex">
              <XCircleIcon className="h-5 w-5 text-red-400" />
              <p className="ml-3 text-sm text-red-700">{error}</p>
            </div>
          </div>
        )}

        {/* Leads Table */}
        {leads.length > 0 && (
          <div className="bg-white shadow overflow-hidden sm:rounded-lg">
            <div className="px-4 py-5 sm:px-6 flex justify-between items-center">
              <div>
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                  Leads Importados
                </h3>
                <p className="mt-1 max-w-2xl text-sm text-gray-500">
                  Selecione os leads para distribuição
                </p>
              </div>
              <div className="flex items-center space-x-4">
                <span className="text-sm text-gray-600">
                  {selectedLeads.length} selecionados
                </span>
                <button
                  onClick={handleDistribute}
                  disabled={selectedLeads.length === 0}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <UserGroupIcon className="h-5 w-5 mr-2" />
                  Distribuir Selecionados
                </button>
              </div>
            </div>
            <div className="border-t border-gray-200">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      <input
                        type="checkbox"
                        className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                        onChange={() => {
                          const allIds = leads.map(lead => lead.id);
                          if (selectedLeads.length === leads.length) {
                            setSelectedLeads([]);
                            setLeads(leads.map(lead => ({ ...lead, selected: false })));
                          } else {
                            setSelectedLeads(allIds);
                            setLeads(leads.map(lead => ({ ...lead, selected: true })));
                          }
                        }}
                        checked={selectedLeads.length === leads.length}
                      />
                    </th>
                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Nome
                    </th>
                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      CNPJ
                    </th>
                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Email
                    </th>
                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Telefone
                    </th>
                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Idade
                    </th>
                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {leads.map((lead) => (
                    <tr key={lead.id} className={lead.selected ? 'bg-primary-50' : ''}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <input
                          type="checkbox"
                          className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                          checked={lead.selected}
                          onChange={() => toggleLeadSelection(lead.id)}
                        />
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-gray-900">{lead.NOME}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{lead.CNPJ}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{lead.EMAIL}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{lead.TELEFONE}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{lead.IDADE}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                          {lead.STATUS}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
