import React, { useState, useEffect } from 'react';
import { auth, db } from '../firebase';
import { 
  collection, 
  addDoc, 
  query, 
  where, 
  orderBy, 
  onSnapshot,
  serverTimestamp,
  deleteDoc,
  doc,
  getDocs
} from 'firebase/firestore';
import { 
  ChartBarIcon, 
  UserGroupIcon, 
  DocumentTextIcon,
  ShareIcon,
  ClockIcon,
  UserIcon,
  BellIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';

export default function DashboardGestor() {
  const [avisos, setAvisos] = useState([]);
  const [novoAviso, setNovoAviso] = useState('');
  const [corretores, setCorretores] = useState([]);
  const [corretorSelecionado, setCorretorSelecionado] = useState('');

  // Carregar corretores
  useEffect(() => {
    const fetchCorretores = async () => {
      const q = query(
        collection(db, 'users'),
        where('role', '==', 'corretor')
      );
      
      const querySnapshot = await getDocs(q);
      const corretoresData = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      
      setCorretores(corretoresData);
    };

    fetchCorretores();
  }, []);

  // Carregar avisos
  useEffect(() => {
    const q = query(
      collection(db, 'avisos'),
      orderBy('timestamp', 'desc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const avisosData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      })).filter(aviso => {
        // Filtrar avisos com mais de 4 horas
        const avisoTime = aviso.timestamp?.toDate();
        if (!avisoTime) return false;
        const horasPassadas = (new Date() - avisoTime) / (1000 * 60 * 60);
        return horasPassadas <= 4;
      });

      setAvisos(avisosData);
    });

    return () => unsubscribe();
  }, []);

  const enviarAviso = async (e) => {
    e.preventDefault();
    if (!novoAviso.trim() || !corretorSelecionado) return;

    try {
      const corretor = corretores.find(c => c.id === corretorSelecionado);
      
      await addDoc(collection(db, 'avisos'), {
        texto: novoAviso.trim(),
        corretorId: corretorSelecionado,
        corretorNome: corretor.name || corretor.email,
        gestorId: auth.currentUser.uid,
        gestorNome: auth.currentUser.displayName || auth.currentUser.email,
        timestamp: serverTimestamp(),
        lido: false
      });

      setNovoAviso('');
      setCorretorSelecionado('');
    } catch (error) {
      console.error('Erro ao enviar aviso:', error);
    }
  };

  const deletarAviso = async (avisoId) => {
    try {
      await deleteDoc(doc(db, 'avisos', avisoId));
    } catch (error) {
      console.error('Erro ao deletar aviso:', error);
    }
  };

  return (
    <div className="container mx-auto px-6 py-8">
      <h3 className="text-3xl font-bold text-gray-900 mb-8">
        Visão Geral
      </h3>

      {/* Cards */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        {/* Total de Leads */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <DocumentTextIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Total de Leads
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    156
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Leads Distribuídos Hoje */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <ShareIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Leads Distribuídos Hoje
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    23
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Leads Devolvidos */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <ClockIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Leads Devolvidos
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    5
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        {/* Leads em Atendimento */}
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <UserIcon className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    Leads em Atendimento
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    45
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Atividade Recente */}
      <div className="mt-8 bg-white shadow overflow-hidden sm:rounded-lg">
        <div className="px-4 py-5 sm:px-6 flex justify-between items-center">
          <h3 className="text-lg leading-6 font-medium text-gray-900">
            Atividade Recente
          </h3>
        </div>
        <div className="border-t border-gray-200">
          <ul className="divide-y divide-gray-200">
            <li className="px-4 py-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <ShareIcon className="h-5 w-5 text-primary-500 mr-3" />
                  <div>
                    <p className="text-sm font-medium text-gray-900">
                      15 leads distribuídos para João Silva
                    </p>
                    <p className="text-sm text-gray-500">
                      Há 2 horas
                    </p>
                  </div>
                </div>
              </div>
            </li>
            <li className="px-4 py-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <ClockIcon className="h-5 w-5 text-primary-500 mr-3" />
                  <div>
                    <p className="text-sm font-medium text-gray-900">
                      3 leads devolvidos por Maria Santos
                    </p>
                    <p className="text-sm text-gray-500">
                      Há 4 horas
                    </p>
                  </div>
                </div>
              </div>
            </li>
          </ul>
        </div>
      </div>

      <div className="max-w-4xl mx-auto space-y-6 mt-8">
        <h1 className="text-2xl font-bold text-gray-900">Dashboard do Gestor</h1>

        {/* Formulário de Novo Aviso */}
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4">Enviar Aviso ao Corretor</h2>
          <form onSubmit={enviarAviso} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Selecione o Corretor
              </label>
              <select
                value={corretorSelecionado}
                onChange={(e) => setCorretorSelecionado(e.target.value)}
                className="w-full p-2 border rounded focus:ring-primary-500 focus:border-primary-500"
                required
              >
                <option value="">Selecione um corretor</option>
                {corretores.map(corretor => (
                  <option key={corretor.id} value={corretor.id}>
                    {corretor.name || corretor.email}
                  </option>
                ))}
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Mensagem
              </label>
              <textarea
                value={novoAviso}
                onChange={(e) => setNovoAviso(e.target.value)}
                className="w-full p-2 border rounded focus:ring-primary-500 focus:border-primary-500"
                rows="3"
                placeholder="Digite o aviso..."
                required
              />
            </div>
            
            <button
              type="submit"
              className="w-full bg-primary-600 text-white py-2 px-4 rounded hover:bg-primary-700 transition-colors"
            >
              Enviar Aviso
            </button>
          </form>
        </div>

        {/* Lista de Avisos */}
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
            <BellIcon className="h-5 w-5 text-primary-500" />
            Avisos Enviados
          </h2>
          
          <div className="space-y-4">
            {avisos.length === 0 ? (
              <p className="text-gray-500 text-center py-4">
                Nenhum aviso enviado nas últimas 4 horas
              </p>
            ) : (
              avisos.map(aviso => (
                <div 
                  key={aviso.id}
                  className="border rounded-lg p-4 relative hover:bg-gray-50"
                >
                  <button
                    onClick={() => deletarAviso(aviso.id)}
                    className="absolute top-2 right-2 text-gray-400 hover:text-red-500"
                  >
                    <XMarkIcon className="h-5 w-5" />
                  </button>
                  
                  <div className="flex items-start justify-between">
                    <div>
                      <p className="font-medium text-gray-900">{aviso.texto}</p>
                      <p className="text-sm text-gray-500 mt-1">
                        Para: {aviso.corretorNome}
                      </p>
                    </div>
                  </div>
                  
                  <div className="mt-2 text-xs text-gray-500">
                    Enviado em: {aviso.timestamp?.toDate().toLocaleString()}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
