import React, { useState, useEffect, useRef } from 'react';
import { auth, db, storage } from '../firebase';
import { 
  collection, 
  query, 
  orderBy, 
  limit, 
  onSnapshot,
  addDoc,
  serverTimestamp,
  where,
  getDocs
} from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { 
  PaperAirplaneIcon,
  PaperClipIcon,
  FaceSmileIcon,
  XMarkIcon,
  DocumentIcon,
  PhotoIcon,
  ArrowDownTrayIcon,
  ChatBubbleLeftEllipsisIcon
} from '@heroicons/react/24/outline';
import data from '@emoji-mart/data';
import Picker from '@emoji-mart/react';

export default function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [showChat, setShowChat] = useState(false);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  // Scroll para a última mensagem
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Carregar usuários
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const usersRef = collection(db, 'users');
        const q = query(usersRef, where('uid', '!=', auth.currentUser.uid));
        const snapshot = await getDocs(q);
        const usersData = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setUsers(usersData);
      } catch (error) {
        console.error('Erro ao carregar usuários:', error);
      }
    };

    fetchUsers();
  }, []);

  // Escutar mensagens
  useEffect(() => {
    if (!selectedUser) return;

    const chatId = [auth.currentUser.uid, selectedUser.id].sort().join('_');
    const messagesRef = collection(db, 'messages');
    const q = query(
      messagesRef,
      where('chatId', '==', chatId),
      orderBy('createdAt', 'asc'),
      limit(50)
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messagesData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setMessages(messagesData);
    });

    return () => unsubscribe();
  }, [selectedUser]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedFile) return;

    try {
      const chatId = [auth.currentUser.uid, selectedUser.id].sort().join('_');
      
      if (selectedFile) {
        await handleFileUpload();
        setSelectedFile(null);
      } else {
        const messageData = {
          text: newMessage.trim(),
          senderId: auth.currentUser.uid,
          receiverId: selectedUser.id,
          chatId,
          createdAt: serverTimestamp(),
        };

        await addDoc(collection(db, 'messages'), messageData);
      }
      
      setNewMessage('');
      setShowEmojiPicker(false);
    } catch (error) {
      console.error('Erro ao enviar mensagem:', error);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e ? e.target.files[0] : selectedFile;
    if (!file) return;

    try {
      setUploading(true);
      const fileRef = ref(storage, `chat_files/${Date.now()}_${file.name}`);
      await uploadBytes(fileRef, file);
      const downloadURL = await getDownloadURL(fileRef);

      const chatId = [auth.currentUser.uid, selectedUser.id].sort().join('_');
      await addDoc(collection(db, 'messages'), {
        fileURL: downloadURL,
        fileName: file.name,
        fileType: file.type,
        senderId: auth.currentUser.uid,
        receiverId: selectedUser.id,
        chatId,
        createdAt: serverTimestamp(),
      });

      if (e) {
        setSelectedFile(null);
      }
    } catch (error) {
      console.error('Erro ao enviar arquivo:', error);
    } finally {
      setUploading(false);
    }
  };

  const handleEmojiSelect = (emoji) => {
    setNewMessage(prev => prev + emoji.native);
  };

  const filteredUsers = users.filter(user => 
    user.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <>
      {/* Botão flutuante do chat */}
      <button
        onClick={() => setShowChat(true)}
        className="fixed bottom-6 right-6 p-4 bg-primary-600 text-white rounded-full shadow-lg hover:bg-primary-700 transition-colors z-50"
      >
        <ChatBubbleLeftEllipsisIcon className="h-6 w-6" />
      </button>

      {/* Modal do Chat */}
      {showChat && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl w-full max-w-4xl h-[80vh] flex overflow-hidden">
            {/* Lista de Usuários */}
            <div className="w-80 border-r border-gray-100 flex flex-col bg-gray-50">
              <div className="p-4 border-b border-gray-100 bg-white">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-gray-900">Chat</h2>
                  <button
                    onClick={() => setShowChat(false)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    <XMarkIcon className="h-5 w-5" />
                  </button>
                </div>
                <input
                  type="text"
                  placeholder="Buscar usuário..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full px-3 py-2 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                />
              </div>
              <div className="flex-1 overflow-y-auto">
                {filteredUsers.length === 0 ? (
                  <div className="p-4 text-center text-gray-500">
                    Nenhum usuário encontrado
                  </div>
                ) : (
                  filteredUsers.map(user => (
                    <button
                      key={user.id}
                      onClick={() => setSelectedUser(user)}
                      className={`w-full p-4 flex items-center space-x-3 hover:bg-white transition-colors ${
                        selectedUser?.id === user.id ? 'bg-white shadow-sm' : ''
                      }`}
                    >
                      <div className="w-10 h-10 bg-gradient-to-br from-primary-500 to-primary-600 rounded-full flex items-center justify-center text-white font-medium">
                        {user.name?.[0]?.toUpperCase() || user.email?.[0]?.toUpperCase() || '?'}
                      </div>
                      <div className="flex-1 text-left">
                        <p className="font-medium text-gray-900 truncate">
                          {user.name || 'Usuário'}
                        </p>
                        <p className="text-sm text-gray-500 truncate">{user.email}</p>
                        <span className="inline-block px-2 py-0.5 text-xs bg-gray-100 text-gray-600 rounded-full mt-1">
                          {user.role === 'gestor' ? 'Gestor' : 'Corretor'}
                        </span>
                      </div>
                    </button>
                  ))
                )}
              </div>
            </div>

            {/* Área do Chat */}
            {selectedUser ? (
              <div className="flex-1 flex flex-col bg-gray-50">
                {/* Cabeçalho do Chat */}
                <div className="p-4 bg-white border-b border-gray-100 flex items-center space-x-3">
                  <div className="w-10 h-10 bg-gradient-to-br from-primary-500 to-primary-600 rounded-full flex items-center justify-center text-white font-medium">
                    {selectedUser.name?.[0]?.toUpperCase() || selectedUser.email?.[0]?.toUpperCase()}
                  </div>
                  <div>
                    <p className="font-medium text-gray-900">{selectedUser.name || 'Usuário'}</p>
                    <p className="text-sm text-gray-500">{selectedUser.email}</p>
                  </div>
                </div>

                {/* Mensagens */}
                <div className="flex-1 overflow-y-auto p-4 space-y-4">
                  {messages.length === 0 ? (
                    <div className="flex items-center justify-center h-full text-gray-500">
                      Nenhuma mensagem ainda. Comece uma conversa!
                    </div>
                  ) : (
                    messages.map(message => (
                      <div
                        key={message.id}
                        className={`flex ${message.senderId === auth.currentUser.uid ? 'justify-end' : 'justify-start'}`}
                      >
                        <div
                          className={`max-w-[70%] rounded-2xl p-3 ${
                            message.senderId === auth.currentUser.uid
                              ? 'bg-primary-600 text-white'
                              : 'bg-white text-gray-900 shadow-sm'
                          }`}
                        >
                          {message.fileURL ? (
                            <div className="space-y-2">
                              <div className="flex items-center space-x-2">
                                {message.fileType?.startsWith('image/') ? (
                                  <PhotoIcon className="h-5 w-5" />
                                ) : (
                                  <DocumentIcon className="h-5 w-5" />
                                )}
                                <span className="text-sm">{message.fileName}</span>
                              </div>
                              {message.fileType?.startsWith('image/') ? (
                                <img
                                  src={message.fileURL}
                                  alt="Imagem"
                                  className="max-w-full rounded-lg"
                                />
                              ) : (
                                <a
                                  href={message.fileURL}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="flex items-center space-x-1 text-sm hover:underline"
                                >
                                  <ArrowDownTrayIcon className="h-4 w-4" />
                                  <span>Baixar arquivo</span>
                                </a>
                              )}
                            </div>
                          ) : (
                            <p className="text-sm whitespace-pre-wrap">{message.text}</p>
                          )}
                          <p className={`text-xs mt-1 ${
                            message.senderId === auth.currentUser.uid
                              ? 'text-primary-100'
                              : 'text-gray-500'
                          }`}>
                            {message.createdAt?.toDate().toLocaleTimeString()}
                          </p>
                        </div>
                      </div>
                    ))
                  )}
                  <div ref={messagesEndRef} />
                </div>

                {/* Input de Mensagem */}
                <div className="p-4 bg-white border-t border-gray-100">
                  <form onSubmit={handleSendMessage} className="flex items-center space-x-2">
                    <button
                      type="button"
                      onClick={() => fileInputRef.current?.click()}
                      className="p-2 text-gray-500 hover:text-gray-700 transition-colors"
                      disabled={uploading}
                    >
                      <PaperClipIcon className="h-5 w-5" />
                    </button>
                    <input
                      type="file"
                      ref={fileInputRef}
                      onChange={(e) => {
                        setSelectedFile(e.target.files[0]);
                        handleFileUpload(e);
                      }}
                      className="hidden"
                    />
                    <div className="relative flex-1">
                      <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Digite sua mensagem..."
                        className="w-full px-4 py-2 bg-gray-50 border border-gray-200 rounded-full focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                      />
                      <button
                        type="button"
                        onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                        className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500 hover:text-gray-700"
                      >
                        <FaceSmileIcon className="h-5 w-5" />
                      </button>
                      {showEmojiPicker && (
                        <div className="absolute bottom-full right-0 mb-2">
                          <div className="relative">
                            <button
                              onClick={() => setShowEmojiPicker(false)}
                              className="absolute -top-2 -right-2 p-1 bg-white rounded-full shadow-lg text-gray-500 hover:text-gray-700 z-10"
                            >
                              <XMarkIcon className="h-4 w-4" />
                            </button>
                            <Picker
                              data={data}
                              onEmojiSelect={handleEmojiSelect}
                              theme="light"
                              set="native"
                            />
                          </div>
                        </div>
                      )}
                    </div>
                    <button
                      type="submit"
                      disabled={!newMessage.trim() && !uploading}
                      className="p-2 text-primary-600 hover:text-primary-700 transition-colors disabled:text-gray-400"
                    >
                      <PaperAirplaneIcon className="h-5 w-5" />
                    </button>
                  </form>
                </div>
              </div>
            ) : (
              <div className="flex-1 flex items-center justify-center text-gray-500 bg-gray-50">
                Selecione um usuário para iniciar uma conversa
              </div>
            )}
          </div>
        </div>
      )}
    </>
  );
}
