import React, { useState, useEffect } from 'react';
import { auth, db } from '../firebase';
import { 
  collection, 
  addDoc, 
  query, 
  orderBy, 
  onSnapshot,
  serverTimestamp,
  where 
} from 'firebase/firestore';

export default function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [users, setUsers] = useState([]);

  // Carregar usuários
  useEffect(() => {
    const usersRef = collection(db, 'users');
    const unsubscribe = onSnapshot(usersRef, (snapshot) => {
      const usersData = snapshot.docs
        .map(doc => ({
          id: doc.id,
          ...doc.data()
        }))
        .filter(user => user.id !== auth.currentUser?.uid);
      setUsers(usersData);
    });

    return () => unsubscribe();
  }, []);

  // Carregar mensagens
  useEffect(() => {
    if (!selectedUser || !auth.currentUser) return;

    const messagesRef = collection(db, 'messages');
    const q = query(
      messagesRef,
      where('participants', 'array-contains', auth.currentUser.uid),
      orderBy('timestamp', 'asc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messagesData = snapshot.docs
        .map(doc => ({
          id: doc.id,
          ...doc.data()
        }))
        .filter(msg => msg.participants.includes(selectedUser.id));
      setMessages(messagesData);
    });

    return () => unsubscribe();
  }, [selectedUser]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedUser || !auth.currentUser) return;

    try {
      await addDoc(collection(db, 'messages'), {
        text: newMessage.trim(),
        senderId: auth.currentUser.uid,
        senderName: auth.currentUser.displayName || auth.currentUser.email,
        participants: [auth.currentUser.uid, selectedUser.id],
        timestamp: serverTimestamp()
      });
      setNewMessage('');
    } catch (error) {
      console.error('Erro ao enviar mensagem:', error);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="grid grid-cols-3 gap-4 h-[600px]">
        {/* Lista de Usuários */}
        <div className="border rounded-lg p-4 overflow-y-auto">
          <h2 className="text-lg font-bold mb-4">Usuários</h2>
          {users.map(user => (
            <button
              key={user.id}
              onClick={() => setSelectedUser(user)}
              className={`w-full p-2 text-left rounded mb-2 ${
                selectedUser?.id === user.id 
                  ? 'bg-blue-100' 
                  : 'hover:bg-gray-100'
              }`}
            >
              <div className="font-medium">{user.name || 'Usuário'}</div>
              <div className="text-sm text-gray-500">{user.email}</div>
            </button>
          ))}
        </div>

        {/* Área de Chat */}
        <div className="col-span-2 border rounded-lg p-4 flex flex-col">
          {selectedUser ? (
            <>
              {/* Cabeçalho */}
              <div className="border-b pb-4 mb-4">
                <h3 className="font-bold">
                  Chat com {selectedUser.name || selectedUser.email}
                </h3>
              </div>

              {/* Mensagens */}
              <div className="flex-1 overflow-y-auto mb-4 space-y-4">
                {messages.map(message => (
                  <div
                    key={message.id}
                    className={`flex ${
                      message.senderId === auth.currentUser?.uid 
                        ? 'justify-end' 
                        : 'justify-start'
                    }`}
                  >
                    <div
                      className={`max-w-[70%] rounded-lg p-3 ${
                        message.senderId === auth.currentUser?.uid
                          ? 'bg-blue-500 text-white'
                          : 'bg-gray-100'
                      }`}
                    >
                      <p>{message.text}</p>
                      <p className="text-xs mt-1 opacity-75">
                        {message.timestamp?.toDate().toLocaleTimeString()}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Input de Mensagem */}
              <form onSubmit={handleSendMessage} className="flex gap-2">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Digite sua mensagem..."
                  className="flex-1 p-2 border rounded"
                />
                <button
                  type="submit"
                  disabled={!newMessage.trim()}
                  className="px-4 py-2 bg-blue-500 text-white rounded disabled:opacity-50"
                >
                  Enviar
                </button>
              </form>
            </>
          ) : (
            <div className="h-full flex items-center justify-center text-gray-500">
              Selecione um usuário para iniciar uma conversa
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
