import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  CloudArrowUpIcon, 
  CheckCircleIcon,
  XCircleIcon,
  UserGroupIcon,
} from '@heroicons/react/24/outline';
import Papa from 'papaparse';

export default function DistribuirLeads() {
  const [leads, setLeads] = useState([]);
  const [selectedLeads, setSelectedLeads] = useState([]);
  const [error, setError] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const navigate = useNavigate();

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    setIsUploading(true);
    setError('');

    if (file) {
      Papa.parse(file, {
        header: true,
        complete: (results) => {
          // Validar as colunas necessárias
          const requiredColumns = ['NOME', 'CNPJ', 'EMAIL', 'TELEFONE', 'IDADE', 'STATUS'];
          const headers = Object.keys(results.data[0]);
          const missingColumns = requiredColumns.filter(col => !headers.includes(col));

          if (missingColumns.length > 0) {
            setError(`Colunas obrigatórias faltando: ${missingColumns.join(', ')}`);
            setIsUploading(false);
            return;
          }

          // Validar e formatar os dados
          const validLeads = results.data.filter(lead => 
            lead.NOME && 
            lead.EMAIL && 
            lead.TELEFONE
          ).map((lead, index) => ({
            ...lead,
            id: index,
            selected: false
          }));

          setLeads(validLeads);
          setIsUploading(false);
        },
        error: (error) => {
          setError('Erro ao processar o arquivo: ' + error.message);
          setIsUploading(false);
        }
      });
    }
  };

  const toggleLeadSelection = (leadId) => {
    setLeads(leads.map(lead => 
      lead.id === leadId ? { ...lead, selected: !lead.selected } : lead
    ));
    
    const lead = leads.find(l => l.id === leadId);
    if (lead) {
      if (lead.selected) {
        setSelectedLeads(selectedLeads.filter(id => id !== leadId));
      } else {
        setSelectedLeads([...selectedLeads, leadId]);
      }
    }
  };

  const handleDistribute = () => {
    // Aqui você implementará a lógica de distribuição
    console.log('Leads selecionados:', selectedLeads.map(id => leads.find(l => l.id === id)));
  };

  return (
    <div className="container mx-auto px-6 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Distribuir Leads</h1>
        <p className="mt-2 text-gray-600">Importe e distribua leads para os corretores</p>
      </div>

      {/* Upload Section */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-8 mb-8">
        <div className="max-w-xl mx-auto text-center">
          <label className="relative cursor-pointer block">
            <input
              type="file"
              className="sr-only"
              accept=".csv"
              onChange={handleFileUpload}
              disabled={isUploading}
            />
            <div className="flex flex-col items-center justify-center space-y-4 p-8 border-2 border-dashed border-gray-200 rounded-xl hover:border-primary-500 transition-colors bg-gray-50">
              <CloudArrowUpIcon className="h-16 w-16 text-gray-400" />
              <div>
                <p className="text-lg font-medium text-gray-700">
                  {isUploading ? 'Processando arquivo...' : 'Clique para fazer upload do CSV'}
                </p>
                <p className="text-sm text-gray-500 mt-1">
                  Colunas necessárias: NOME, CNPJ, EMAIL, TELEFONE, IDADE, STATUS
                </p>
              </div>
            </div>
          </label>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-8 rounded-r-xl">
          <div className="flex items-center">
            <XCircleIcon className="h-5 w-5 text-red-400" />
            <p className="ml-3 text-sm text-red-700">{error}</p>
          </div>
        </div>
      )}

      {/* Leads List */}
      {leads.length > 0 && (
        <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
          <div className="p-6 border-b border-gray-100">
            <div className="flex justify-between items-center">
              <div>
                <h3 className="text-lg font-semibold text-gray-900">
                  Leads Importados
                </h3>
                <p className="text-sm text-gray-500 mt-1">
                  {selectedLeads.length} leads selecionados de {leads.length}
                </p>
              </div>
              <button
                onClick={handleDistribute}
                disabled={selectedLeads.length === 0}
                className="inline-flex items-center px-4 py-2 rounded-lg shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                <UserGroupIcon className="h-5 w-5 mr-2" />
                Distribuir Selecionados
              </button>
            </div>
          </div>
          <div className="overflow-x-auto">
            <div className="inline-block min-w-full align-middle">
              <div className="overflow-hidden">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th scope="col" className="px-6 py-3 text-left">
                        <input
                          type="checkbox"
                          className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded transition-colors"
                          onChange={() => {
                            const allIds = leads.map(lead => lead.id);
                            if (selectedLeads.length === leads.length) {
                              setSelectedLeads([]);
                              setLeads(leads.map(lead => ({ ...lead, selected: false })));
                            } else {
                              setSelectedLeads(allIds);
                              setLeads(leads.map(lead => ({ ...lead, selected: true })));
                            }
                          }}
                          checked={selectedLeads.length === leads.length}
                        />
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Nome
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        CNPJ
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Email
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Telefone
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Idade
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Status
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {leads.map((lead) => (
                      <tr 
                        key={lead.id} 
                        className={`${lead.selected ? 'bg-primary-50' : 'hover:bg-gray-50'} transition-colors cursor-pointer`}
                        onClick={() => toggleLeadSelection(lead.id)}
                      >
                        <td className="px-6 py-4 whitespace-nowrap">
                          <input
                            type="checkbox"
                            className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded transition-colors"
                            checked={lead.selected}
                            onChange={(e) => e.stopPropagation()}
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">{lead.NOME}</div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm text-gray-500">{lead.CNPJ}</div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm text-gray-500">{lead.EMAIL}</div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm text-gray-500">{lead.TELEFONE}</div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm text-gray-500">{lead.IDADE}</div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="px-3 py-1 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                            {lead.STATUS}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
